// WhitelabelERC20 Contract Bytecode for deployment
// This bytecode should be generated by compiling the WhitelabelERC20.sol contract

import { AbiCoder } from 'ethers';

export const WHITELABEL_ERC20_BYTECODE =
  '';

/**
 * Encodes the constructor parameters for the WhitelabelERC20 contract
 *
 * @param name The name of the token
 * @param symbol The symbol of the token
 * @param initialHolder The address that will receive the initial supply
 * @param initialSupply The initial supply of tokens (as a decimal string, will be converted to wei)
 * @returns The encoded constructor parameters
 */
export const encodeWhitelabelERC20Constructor = (
  name: string,
  symbol: string,
  initialHolder: string,
  initialSupply: string
): string => {
  const abiCoder = new AbiCoder();

  // The constructor takes 4 parameters: name, symbol, initialHolder, initialSupply
  return abiCoder
    .encode(
      ['string', 'string', 'address', 'uint256'],
      [name, symbol, initialHolder, initialSupply]
    )
    .slice(2); // Remove the '0x' prefix
};

/**
 * Returns the complete creation code for the WhitelabelERC20 contract (bytecode + encoded constructor arguments)
 *
 * @param name The name of the token
 * @param symbol The symbol of the token
 * @param initialHolder The address that will receive the initial supply
 * @param initialSupply The initial supply of tokens (as a decimal string, will be converted to wei)
 * @returns The complete contract creation code
 */
export const getWhitelabelERC20CreationCode = (
  name: string,
  symbol: string,
  initialHolder: string,
  initialSupply: string
): string => {
  return (
    WHITELABEL_ERC20_BYTECODE +
    encodeWhitelabelERC20Constructor(name, symbol, initialHolder, initialSupply)
  );
};
